import React from 'react';
import Avatar from '@mui/material/Avatar';
import Grid from '@mui/material/Grid';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Send } from '@mui/icons-material';


const MessageRoom = () => {
  const [message, setMessage] = React.useState('');
  const [messages, setMessages] = [
    { id: 1, user: 'User1', text: 'Hello there!' },
    { id: 2, user: 'User2', text: 'Hi! How are you doing?' },
    { id: 3, user: 'User1', text: 'Great! Thanks for asking.' },
  ];

  const handleInputChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSend = () => {
    // construct a new message from the state value
    const newMessage = {
      id: messages.length + 1, // or any other UID
      user: 'User1',
      text: message,
    };

    // update the messages with the new message
    setMessages([ ...messages, newMessage ]);

    // reset the field for the next message
    setMessage('');
  };


  const handleKeyDown = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSend();
    }
  };

  return (
    <div>
      <List>
        {messages.map((msg) => (
          <ListItem key={msg.id}>
            <Grid
              container
              direction={msg.user === 'User1' ? 'row' : 'row-reverse'}
              justifyContent={msg.user === 'User1' ? 'flex-end' : 'flex-start'}
              rowSpacing={1}
              columnSpacing={1}
              alignItems='center'
              sx={{backgroundColor: msg.user === 'User2' ? '#e0e0e0' : 'transparent',}}
            >
              <Grid item style={{ flexGrow: 1 }}>
                <Box sx={{ p: 1, borderRadius: 1 }}>
                  <ListItemText primary={msg.user} secondary={msg.text} style={{ textAlign: msg.user === 'User1' ? 'right' : 'left' }} />
                </Box>
              </Grid>
              <Grid item>
                <ListItemAvatar>
                  <Avatar>{msg.user[0]}</Avatar>
                </ListItemAvatar>
              </Grid>
            </Grid>
          </ListItem>
        ))}
      </List>
      <Box>
        <TextField
          fullWidth
          value={message}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          variant='outlined'
          placeholder='Type your message...'
          multiline
          InputProps={{
            endAdornment: (
              <Send onClick={handleSend} style={{ cursor: 'pointer' }} />
            ),
          }}
        />
      </Box>
    </div>
  );
};

export default MessageRoom;
