import React from 'react';
import { Avatar, List, ListItem, ListItemAvatar, ListItemText, TextField } from '@mui/material';
import { Send } from '@mui/icons-material';

const messages = [
  {id: 1, user: 'User1', text: 'Hello there!'},
  {id: 2, user: 'User2', text: 'Hi! How are you doing?'},
  {id: 3, user: 'User1', text: 'Great! Thanks for asking.'},
];

const MessageRoom = () => {
  const [message, setMessage] = React.useState('');

  const handleInputChange = event => {
    setMessage(event.target.value);
  }

  const handleSend = () => {
    console.log(message);  // Log the message or handle send event here
    setMessage('');  // Clear input box
  }

  return (
    <div>
      <List>
        {messages.map((message) => (
          <ListItem key={message.id} style={{ justifyContent: message.user === 'User1' ? 'flex-left' : 'flex-right' }}>
            <ListItemAvatar>
              <Avatar>{message.user[0]}</Avatar>
            </ListItemAvatar>
            <ListItemText primary={message.user} secondary={message.text} />
          </ListItem>
        ))}
      </List>
      <div>
        <TextField
          fullWidth
          value={message}
          onChange={handleInputChange}
          variant="outlined"
          placeholder="Type your message..."
          InputProps={{
            endAdornment: (
              <Send onClick={handleSend} style={{cursor: 'pointer'}} />
            ),
          }}
        />
      </div>
    </div>
  );
}

export default MessageRoom;
