import React from 'react';
import Avatar from '@mui/material/Avatar';
import Grid from '@mui/material/Grid';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Send } from '@mui/icons-material';

const MessageRoom = () => {
  const [message, setMessage] = React.useState('');

  const messages = [
    { id: 1, user: 'User1', text: 'Hello there!' },
    { id: 2, user: 'User2', text: 'Hi! How are you doing?' },
    { id: 3, user: 'User1', text: 'Great! Thanks for asking.' },
  ];

  const handleInputChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSend = () => {
    // Add your message sending logic here
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSend();
    }
  };

  return (
    <div>
      <List>
        {messages.map((message) => (
          <ListItem key={message.id}>
            <Grid
              container
              direction="row"
              justifyContent={message.user === 'User1' ? 'flex-end' : 'flex-start'}
            >
              <Grid item>
                <ListItemAvatar>
                  <Avatar>{message.user[0]}</Avatar>
                </ListItemAvatar>
              </Grid>
              <Grid 
                item 
                style={{ textAlign: message.user === 'User1' ? 'right' : 'left' }}
              >
                <ListItemText primary={message.user} secondary={message.text} />
              </Grid>
            </Grid>
          </ListItem>
        ))}
      </List>
      <Box>
        <TextField
          fullWidth
          value={message}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          variant='outlined'
          placeholder='Type your message...'
          multiline
          InputProps={{
            endAdornment: (
              <Send onClick={handleSend} style={{ cursor: 'pointer' }} />
            ),
          }}
        />
      </Box>
    </div>
  );
};

export default MessageRoom;
