import React from 'react';
import Avatar from '@mui/material/Avatar';
import Grid from '@mui/material/Grid';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import ListItemText from '@mui/material/ListItemText';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Send } from '@mui/icons-material';

const MessageRoom = () => {
  const [message, setMessage] = React.useState('');

  const handleInputChange = (event) => {
    setMessage(event.target.value);
  };

  const handleSend = () => {
    // Add your message sending logic here
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter' && !event.shiftKey) {
      event.preventDefault();
      handleSend();
    }
  };

  return (
    <div>
      <List>
        {messages.map((message) => (
          <ListItem key={message.id}>
            <Grid
              container
              direction={message.user === 'User1' ? 'row-reverse' : 'row'}
              justifyContent={message.user === 'User1' ? 'flex-end' : 'flex-start'}
            >
              <ListItemAvatar>
                <Avatar>{message.user[0]}</Avatar>
              </ListItemAvatar>
              <ListItemText primary={message.user} secondary={message.text} />
            </Grid>
          </ListItem>
        ))}
      </List>
      <Box>
        <TextField
          fullWidth
          value={message}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          variant='outlined'
          placeholder='Type your message...'
          multiline
          InputProps={{
            endAdornment: (
              <Send onClick={handleSend} style={{ cursor: 'pointer' }} />
            ),
          }}
        />
      </Box>
    </div>
  );
};

export default MessageRoom;
